import React from "react";
import "../styles/Hero.css";

const Hero = () => {
  return (
    <div className="hero__container">
      <h1 className="hero__header">EasyChat</h1>
      <p className="hero__description">
        Your one-stop solution, for all you chat needs.
      </p>
    </div>
  );
};

export default Hero;
